    ret.e = { v: txout.satoshis, i }
    ret.i = i
    return ret
  }

  const tx = new Tx(rawtx)
  ret.in = tx.inputs.map(input)
  ret.out = tx.outputs.map(output)
  ret.lock = tx.nLockTime
  return ret
}

