function asm (s) {
  const parts = s.split(' ')
  let out = []

  for (const part of parts) {
    // If one of our predefined op-codes
    if (typeof asm.OP_CODES[part] !== 'undefined') {
      out.push(asm.OP_CODES[part])
      continue
    }

    // Hex data
    const bytes = Hex.stringToBytes(part.length === 1 ? '0' + part : part)

    // OP_0
    if (bytes[0] === 0) {
      out.push(bytes[0]) // OP_0
      continue
    }

    // OP_1-OP_16
    if (bytes.length === 1 && bytes[0] >= 1 && bytes[0] <= 16) {
      out.push(bytes[0] + 0x50)
      continue
    }

    // OP_PUSH+[1-75] <bytes>
    if (bytes.length <= 75) {
      out = out.concat(bytes.length).concat(bytes)
      continue
    }

    // OP_PUSHDATA1 <len> <bytes>
    if (bytes.length < 256) {
      out = out.concat(asm.OP_CODES.OP_PUSHDATA1).concat([bytes.length]).concat(bytes)
      continue
    }

    const floor = x => parseInt(x.toString(), 10)

    // OP_PUSHDATA2 <len> <bytes>
    if (bytes.length < 256 * 256) {
      const len = [floor(bytes.length / 256), bytes.length % 256]
      out = out.concat(asm.OP_CODES.OP_PUSHDATA2).concat(len).concat(bytes)
      continue
    }

    // OP_PUSHDATA4 <len> <bytes>
    const len = [
      floor(bytes.length / 256 / 256 / 256),
      floor(bytes.length / 256 / 256) % 256,
      floor(bytes.length / 256) % 256,
      bytes.length % 256
    ]
    out = out.concat(asm.OP_CODES.OP_PUSHDATA4).concat(len).concat(bytes)
    continue
  }

  return Hex.bytesToString(out)
}

